import 'package:flutter/material.dart';
import 'package:provider/provider.dart';
import 'package:lucasbeatsfederacao/services/stats_service.dart';
import 'package:lucasbeatsfederacao/services/admin_service.dart';
import 'package:lucasbeatsfederacao/utils/logger.dart';

class AdminReportsScreen extends StatefulWidget {
  const AdminReportsScreen({super.key});

    @override
      State<AdminReportsScreen> createState() => _AdminReportsScreenState();
      }

      class _AdminReportsScreenState extends State<AdminReportsScreen> {
        String _selectedPeriod = 'Últimos 7 dias';
          bool _isLoading = false;
            Map<String, dynamic> _reportData = {};
              String? _error;

                // Variáveis para armazenar os dados já tratados como String
                  String totalUsersString = '0';
                    String onlineUsersString = '0';
                      String newRegistrationsString = '0';
                        String bannedUsersString = '0';
                          String messagesSentString = '0';
                            String callsMadeString = '0';
                              String avgOnlineTimeString = 'N/A';
                                String channelsCreatedString = 'N/A';
                                  String totalFederationsString = '0';
                                    String totalClansString = '0';
                                      String activeClansString = '0';
                                        String avgMembersPerClanString = 'N/A';
                                          String serverUptimeString = 'N/A';
                                            String cpuUsageString = 'N/A';
                                              String memoryUsageString = 'N/A';
                                                String storageUsedString = 'N/A';

                                                  final List<String> _periods = [
                                                      'Últimas 24 horas',
                                                          'Últimos 7 dias',
                                                              'Últimos 30 dias',
                                                                  'Últimos 3 meses',
                                                                      'Último ano',
                                                                        ];

                                                                          @override
                                                                            void initState() {
                                                                                super.initState();
                                                                                    _loadReportData();
                                                                                      }

                                                                                        void _loadReportData() async {
                                                                                            setState(() {
                                                                                                  _isLoading = true;
                                                                                                        _error = null;
                                                                                                            });

                                                                                                                try {
                                                                                                                      final statsService = Provider.of<StatsService>(context, listen: false);
                                                                                                                            final adminService = Provider.of<AdminService>(context, listen: false);

                                                                                                                                  final globalStats = await statsService.getGlobalStats();
                                                                                                                                        final systemLogs = await adminService.getSystemLogs();

                                                                                                                                              if (mounted) {
                                                                                                                                                      setState(() {
                                                                                                                                                                // PASSO 4: Bloco de código adicionado para criar variáveis intermediárias
                                                                                                                                                                          // e tratar os dados antes de usá-los.

                                                                                                                                                                                    // Variáveis para Relatório de Usuários
                                                                                                                                                                                              final totalUsers = globalStats['totalUsers'];
                                                                                                                                                                                                        totalUsersString = totalUsers != null ? totalUsers.toString() : '0';

                                                                                                                                                                                                                  final onlineUsers = globalStats['onlineUsers'];
                                                                                                                                                                                                                            onlineUsersString = onlineUsers != null ? onlineUsers.toString() : '0';

                                                                                                                                                                                                                                      final newRegistrations = globalStats['newRegistrations'];
                                                                                                                                                                                                                                                newRegistrationsString = newRegistrations != null ? newRegistrations.toString() : '0';

                                                                                                                                                                                                                                                          final bannedUsers = globalStats['bannedUsers'];
                                                                                                                                                                                                                                                                    bannedUsersString = bannedUsers != null ? bannedUsers.toString() : '0';

                                                                                                                                                                                                                                                                              // Variáveis para Relatório de Atividade
                                                                                                                                                                                                                                                                                        final messagesSent = globalStats['totalMessages'];
                                                                                                                                                                                                                                                                                                  messagesSentString = messagesSent != null ? messagesSent.toString() : '0';

                                                                                                                                                                                                                                                                                                            final callsMade = globalStats['activeCalls'];
                                                                                                                                                                                                                                                                                                                      callsMadeString = callsMade != null ? callsMade.toString() : '0';

                                                                                                                                                                                                                                                                                                                                final avgOnlineTime = globalStats['avgOnlineTime'];
                                                                                                                                                                                                                                                                                                                                          avgOnlineTimeString = avgOnlineTime != null ? avgOnlineTime.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                    final channelsCreated = globalStats['channelsCreated'];
                                                                                                                                                                                                                                                                                                                                                              channelsCreatedString = channelsCreated != null ? channelsCreated.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                                        // Variáveis para Relatório de Organizações
                                                                                                                                                                                                                                                                                                                                                                                  final totalFederations = globalStats['totalFederations'];
                                                                                                                                                                                                                                                                                                                                                                                            totalFederationsString = totalFederations != null ? totalFederations.toString() : '0';

                                                                                                                                                                                                                                                                                                                                                                                                      final totalClans = globalStats['totalClans'];
                                                                                                                                                                                                                                                                                                                                                                                                                totalClansString = totalClans != null ? totalClans.toString() : '0';

                                                                                                                                                                                                                                                                                                                                                                                                                          final activeClans = globalStats['activeClans'];
                                                                                                                                                                                                                                                                                                                                                                                                                                    activeClansString = activeClans != null ? activeClans.toString() : '0';

                                                                                                                                                                                                                                                                                                                                                                                                                                              final avgMembersPerClan = globalStats['avgMembersPerClan'];
                                                                                                                                                                                                                                                                                                                                                                                                                                                        avgMembersPerClanString = avgMembersPerClan != null ? avgMembersPerClan.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                                                                                                                                  // Variáveis para Relatório do Sistema
                                                                                                                                                                                                                                                                                                                                                                                                                                                                            final serverUptime = systemLogs.isNotEmpty ? systemLogs[0]['serverUptime'] : null;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      serverUptimeString = serverUptime != null ? serverUptime.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                final cpuUsage = systemLogs.isNotEmpty ? systemLogs[0]['cpuUsage'] : null;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          cpuUsageString = cpuUsage != null ? cpuUsage.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    final memoryUsage = systemLogs.isNotEmpty ? systemLogs[0]['memoryUsage'] : null;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              memoryUsageString = memoryUsage != null ? memoryUsage.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        final storageUsed = systemLogs.isNotEmpty ? systemLogs[0]['storageUsed'] : null;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  storageUsedString = storageUsed != null ? storageUsed.toString() : 'N/A';

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            // PASSO 5: O mapa _reportData agora é preenchido com as variáveis String seguras.
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      // Embora não seja estritamente necessário após a refatoração, mantê-lo pode ser útil
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                // para outras funcionalidades (como exportação) que possam ler diretamente dele.
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          _reportData = {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      'userReports': {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    'totalUsers': totalUsersString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  'onlineUsers': onlineUsersString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                'newRegistrations': newRegistrationsString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              'bannedUsers': bannedUsersString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          },
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      'activityReports': {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    'messagesSent': messagesSentString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  'callsMade': callsMadeString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                'avgOnlineTime': avgOnlineTimeString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              'channelsCreated': channelsCreatedString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          },
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      'organizationReports': {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    'totalFederations': totalFederationsString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  'totalClans': totalClansString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                'activeClans': activeClansString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              'avgMembersPerClan': avgMembersPerClanString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          },
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      'systemReports': {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    'serverUptime': serverUptimeString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  'cpuUsage': cpuUsageString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                'memoryUsage': memoryUsageString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              'storageUsed': storageUsedString,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          },
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    };
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              _isLoading = false;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      });
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                } catch (e, stackTrace) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      Logger.error('Erro ao carregar dados do relatório', error: e, stackTrace: stackTrace);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            if (mounted) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    setState(() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              _error = 'Erro ao carregar dados do relatório: ${e.toString()}';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        _isLoading = false;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                });
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }

